from art import logoimport randomdef deal_card() -> int:    cards = [11, 2, 3, 4, 5, 6, 7, 8, 9, 10, 10, 10, 10]    return random.choice(cards)def calculate_score(card_deck: list) -> (list, int):    points = sum(card_deck)    if len(card_deck) == 2 and points == 21:        return card_deck, 0    if points > 21 and 11 in card_deck:        card_deck.remove(11)        card_deck.append(1)        points = sum(card_deck)    return card_deck, pointsdef compare(user_score: int, computer_score: int, user_cards: list, computer_cards: list):    if user_score == computer_score:        print("\nIt's a draw ðŸ™ƒ")    elif user_score == 0:        print(f"\nYou win with a Blackjack ðŸ˜Ž")    elif computer_score == 0:        print("\nYou lose. Dealer has a Blackjack ðŸ˜±")    elif user_score > 21:        print("\nYou went over. You lose ðŸ˜­")    elif computer_score > 21:        print("\nDealer went over. You win ðŸ˜")    else:        if user_score > computer_score:            print("\nYou win ðŸ˜€")        if user_score < computer_score:            print("\nYou lose ðŸ˜¤")    print(f"Player's final hand: {user_cards} - Player's final score: {user_score}")    print(f"Dealer final hand: {computer_cards} - Dealer final score: {computer_score}\n")def blackjack_game():    print(logo)    print("Welcome to the Blackjack table!")    player_hand = []    dealer_hand = []    for card in range(2):        player_hand.append(deal_card())        dealer_hand.append(deal_card())    # player_hand = [11, 10]    # dealer_hand = [10, 11]    player_hand, player_score = calculate_score(player_hand)    dealer_hand, dealer_score = calculate_score(dealer_hand)    if player_score == 0 or dealer_score == 0:        compare(player_score, dealer_score, player_hand, dealer_hand)        return    print(f"Player hand: {player_hand} - Player Score: {player_score}")    print(f"Dealer's first card {dealer_hand[0]}")    hit = True    while hit:        draw_card = input("Type 'y' to get another card, type 'n' to pass: ").lower()        while draw_card != "y" and draw_card != "n":            print("Invalid input.")            draw_card = input("Type 'y' to get another card, type 'n' to pass: ").lower()        if draw_card == "y":            player_hand.append(deal_card())            player_hand, player_score = calculate_score(player_hand)            if player_score > 21:                compare(player_score, dealer_score, player_hand, dealer_hand)                return            print(f"\nPlayer hand: {player_hand} - Player Score: {player_score}")            print(f"Dealer's first card {dealer_hand[0]}\n")        elif draw_card == "n":            hit = False            while dealer_score != 0 and dealer_score <= 16:                dealer_hand.append(deal_card())                dealer_hand, dealer_score = calculate_score(dealer_hand)                if dealer_score > 21:                    compare(player_score, dealer_score, player_hand, dealer_hand)                    return    compare(player_score, dealer_score, player_hand, dealer_hand)proceed = Truewhile proceed:    play_game = input("Do you want to play a game of blackjack? "                      "Type 'y' or 'n': ").lower()    while play_game != "y" and play_game != "n":        print("Invalid input.")        play_game = input("Do you want to play a game of blackjack? "                          "Type 'y' or 'n': ").lower()    if play_game == "y":        print("\n" * 100)        blackjack_game()    else:        print("Goodbye ðŸ‘‹")        proceed = False